function mutation(arr) {
  let str1 = arr[0].toString().toLowerCase();
  let str2 = arr[1].toString().toLowerCase();
  let arr1 = str1.slice('');
  let arr2 = str2.slice('');
  for(let i = 0; i < arr2.length; i++) {
    if(arr1.indexOf(arr2[i]) === -1)
      return false;
  }
  return true;
}

mutation(["hello", "hey"]);
