function largestOfFour(arr) {
  // You can do this!
  var largest = 0;
  let newArr = [];
  for(let i=0;i < arr.length;i++) {
    for(let j=0;j < arr[i].length;j++) {
      if(j === 0) {
        largest = arr[i][j];
        continue;
      }
      if(arr[i][j] > largest)
        largest = arr[i][j];
    }
    newArr.push(largest);
  }
  console.log(newArr);
  return newArr;
}

