String.prototype.replaceAt = function(index, character) {
    return this.substr(0, index) + character + this.substr(index+character.length);
};

function titleCase(str) {
  str = str.toLowerCase();
  let arr = str.split(" ");
  let newArr = [];
  for(let i = 0; i < arr.length; i++) {
    newArr[i] = arr[i].substr(0,0) + arr[i].charAt(0).toUpperCase() + arr[i].substr(0+arr[i].charAt(0).toUpperCase().length);
  }
  str = newArr.join(" ");
  return str;
}

