function translatePigLatin(str) {
  let regex = /[a,e,i,o,u]/;
  if(str.charAt(0).match(regex) !== null) {
    str += "way";
  } else {
    if(str.match(regex) === null) {
      return str + "ay";
    } else {
      for(let i = 1; i < str.length; i++) {
        if(str.charAt(i).match(regex) !== null) {
          return str.substr(i) + str.substr(0,i) + "ay"; 
        }
      }
    }
  }
  console.log(str);
  return str;
}

