function rot13(str) { // LBH QVQ VG!
  let regex = /[A-Z]/;
  let arr = str.split('');
  for(let i = 0; i < arr.length; i++) {
    if(regex.test(arr[i])) {
      arr[i] = arr[i].charCodeAt(0);
      arr[i] += 13;
    if(arr[i] > 90) {
      arr[i] -= 26;
    }
    arr[i] = String.fromCharCode(arr[i]);
    }
  }
  str = arr.join('');
  console.log(str);
  return str;
}

