function palindrome(str) {
  // Good luck!
  str = str.replace(/\W|\s|_/gi, '').toLowerCase();
  console.log(str);
  let arr = str.split('');
  let revArr = arr.slice().reverse();
  for(let i = 0; i < arr.length; i++) {
    if(arr[i] !== revArr[i])
      return false;
  }
  return true;
}



